						const frontmatter$2v = {"title":"A Guide to DeepSpeech Speech to Text","description":"DeepSpeech is a Python library for doing ASR. In this post, well look at how to use DeepSpeech to do Speech to Text in Python","date":"2022-08-01T00:00:00.000Z","cover":"https://res.cloudinary.com/deepgram/image/upload/v1659364680/blog/2022/08/guide-deepspeech-speech-to-text/cover.jpg","authors":["yujian-tang"],"category":"tutorial","tags":["python","deepspeech"],"seo":{"title":"A Guide to DeepSpeech Speech to Text","description":"DeepSpeech is a Python library for doing ASR. In this post, well look at how to use DeepSpeech to do Speech to Text in Python"},"shorturls":{"share":"https://dpgr.am/2b36a93","twitter":"https://dpgr.am/3d69f41","linkedin":"https://dpgr.am/d6f2658","reddit":"https://dpgr.am/2dc3fb8","facebook":"https://dpgr.am/7056571"},"og":{"image":"https://res.cloudinary.com/deepgram/image/upload/v1661454117/blog/guide-deepspeech-speech-to-text/ograph.png"}};
