						const frontmatter$2b = {"title":"How Voice Technology Creates a More Accessible World [7 Ways]","description":"Voice tech isnt just shaking up enterprises; its also providing real quality-of-life improvements for people. Here are 7 examples.","date":"2022-04-26T00:00:00.000Z","cover":"https://res.cloudinary.com/deepgram/image/upload/v1661981419/blog/how-voice-technology-creates-accessible-world/how-voice-tech-creates-more-accessible-world-thumb.png","authors":["chris-doty"],"category":"speech-trends","tags":["inclusion"],"seo":{"title":"How Voice Technology Creates a More Accessible World [7 Ways]","description":"Voice tech isnt just shaking up enterprises; its also providing real quality-of-life improvements for people. Here are 7 examples."},"og":{"image":"https://res.cloudinary.com/deepgram/image/upload/v1661981419/blog/how-voice-technology-creates-accessible-world/how-voice-tech-creates-more-accessible-world-thumb.png"},"shorturls":{"share":"https://dpgr.am/2434c02","twitter":"https://dpgr.am/5da4554","linkedin":"https://dpgr.am/abbb9ba","reddit":"https://dpgr.am/af3a26b","facebook":"https://dpgr.am/c74c058"}};
						const file$2b = "/Users/sandrarodgers/web-next/blog/src/content/blog/posts/how-voice-technology-creates-accessible-world/index.md";
