						const frontmatter$H = {"title":"Top Six Uses Cases for Automatic Speech Recognition (ASR) in Social Media","description":"The social media industry is full of use cases for ASR and NLUhere are the top six, and how these solutions drive business value.","date":"2022-10-17T22:55:19.866Z","cover":"https://res.cloudinary.com/deepgram/image/upload/v1666047379/blog/Top%20Six%20Uses%20Cases%20for%20Automatic%20Speech%20Recognition%20%28ASR%29%20in%20Social%20Media/how-ASR-supports-content-moderation-companies-thumb-554x220_uzmvz9.png","authors":["chris-doty"],"category":"best-practice","tags":["social"],"shorturls":{"share":"https://dpgr.am/d93ac78","twitter":"https://dpgr.am/5b6f9dc","linkedin":"https://dpgr.am/eae57cd","reddit":"https://dpgr.am/ca9e72f","facebook":"https://dpgr.am/4c0d76d"}};
						const file$H = "/Users/sandrarodgers/web-next/blog/src/content/blog/posts/top-six-uses-cases-for-asr-social-media/index.md";
